/*==================================================
AllCampusesView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display all campuses.
================================================== */
import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import "./AllCampusesView.css";
const AllCampusesView = (props) => {
  const {deleteCampus} = props;
  console.log(props);
  // If there is no campus, display a message.
  if (!props.allCampuses.length) {
    return <div>There are no campuses.</div>;
  }

  // If there is at least one campus, render All Campuses view 
  return (
    <div>
      <h1>All Campuses</h1>

      <div id="campus-container">
        {props.allCampuses.map((campus) => (
          <div key={campus.id} className="campus">
            {campus.imageurl === "" ? <img className="campus-image" src="https://cdn-icons-png.flaticon.com/512/904/904861.png" alt="campus"></img> : <img src={campus.imageurl} alt="campus"></img>}
            <div id="campus-info-container">
              <div style={{display:"flex", justifyContent:"space-between"}}>
                <Link to={`/campus/${campus.id}`}>
                  <h2>{campus.name}</h2>
                </Link>
                <h4>campus id: {campus.id}</h4>
              </div>
              <div>
                <p>{campus.address}</p>
                <p>{campus.description}</p>
              </div>
            </div>
            <div className="buttons-container">
              <button>Edit</button>
              <button id="campus-delete-button" onClick={() => deleteCampus(campus.id)}>Delete</button>
            </div>
          </div>
        ))}
      </div>
      <br/>
      <Link to={`/newcampus`}>
        <button>Add New Campus</button>
      </Link>
      <br/><br/>
    </div>
  );
};

// Validate data type of the props passed to component.
AllCampusesView.propTypes = {
  allCampuses: PropTypes.array.isRequired,
};

export default AllCampusesView;
