import "./EditStudentView.css"
const EditStudentView = (props) => {
    const {student, handleChange, handleSubmit, campuses } = props;
    console.log(student);

    return (
        <div>
          <h1>Edit Student</h1>
    
          <div className="">
            <div className="">
              <div className="edit-student-title">
                  {student.firstname} {student.lastname}
              </div>
              <form style={{textAlign: 'center'}} onSubmit={(e) => handleSubmit(e)}>
                <label style= {{color:'#11153e', fontWeight: 'bold'}}>First Name: </label>
                <input type="text" name="firstname" onChange ={(e) => handleChange(e)} />
    
                <label style={{color:'#11153e', fontWeight: 'bold'}}>Last Name: </label>
                <input type="text" name="lastname" onChange={(e) => handleChange(e)} />
    
                <label style={{color:'#11153e', fontWeight: 'bold'}}>Campus: </label>
                <select name="campusId" onChange={(e) => handleChange(e)}>
                  {
                    campuses.map((campus) => {
                      return <option key={campus.id} value={campus.id}>
                              {campus.name.length > 30 ? `${campus.name.slice(0, 20)}...` : campus.name}
                              </option>
                    })
                  }
                </select>

                <label style={{color:'#11153e', fontWeight: 'bold'}}>Email: </label>
                <input type="email" name="email" onChange={(e) => handleChange(e)} />

                <label style={{color:'#11153e', fontWeight: 'bold'}}>Image Url: </label>
                <input type="text" name="imageUrl" onChange={(e) => handleChange(e)} />

                <label style={{color:'#11153e', fontWeight: 'bold'}}>GPA: </label>
                <input type="number" name="gpa" min="0.0" max="4.0" step="0.1" onChange={(e) => handleChange(e)} />
                <button type="submit">
                  Submit
                </button>
              </form>
              </div>
          </div>
        </div>    
      )
}

export default EditStudentView;
